/*program ke-3*/import java.util.Scanner;public class InputOutput{public static void main(String []args){	Scanner input = new Scanner (System.in);	String nama;	char jenisKelamin;	int tinggiBadan;	boolean menikah;		System.out.println("masukan Nama : ");		nama	= input.nextLine();			System.out.println("masukan Jenis Kelamin : ");		jenisKelamin	= input.nextLine().charAt(0);			System.out.println("masukan Tinggi Badan : ");		tinggiBadan	= input.nextInt();			System.out.println("masukan Status : ");		menikah	= input.nextBoolean();			}}